          <label>Date</label>
          <input placeholder="Date" onChange={(e): void => setName(e.target.value)} />
        </Form.Field>
        <Form.Field>
          <label>Mushroom</label>
          <input placeholder="Date" onChange={(e): void => setName(e.target.value)} />
        </Form.Field>
        <Form.Field>
          <label>Quantity</label>
          <input placeholder="Date" onChange={(e): void => setName(e.target.value)} />
        </Form.Field>
        <Button type="submit" primary>
          Save
        </Button>
      </Form>
    </>
  );
}
