import React, { useEffect } from 'react';
import { Item as SemanticItem } from 'semantic-ui-react';
import { Container as SemanticContainer } from 'semantic-ui-react';
import { GetServerSideProps } from 'next';
import Head from 'next/head';
import { Circle, CircleMarker, Map, Polygon, Popup, TileLayer } from 'react-leaflet';

import Location from './models/Location';
import getLocation from './queries/getLocation';

import Item from './components/Item';

type LocationProps = {
  location: Location;
};

const center = [51.505, -0.09];

const polygon = [
  [51.515, -0.09],
  [51.52, -0.1],
  [51.52, -0.12],
];

const multiPolygon = [
  [
    [51.51, -0.12],
    [51.51, -0.13],
    [51.53, -0.13],
  ],
  [
    [51.51, -0.05],
    [51.51, -0.07],
    [51.53, -0.07],
  ],
];

export default function LocationDetails({ location }: LocationProps): JSX.Element {
  useEffect(() => {
    // const mymap = L.map('mapid').setView([51.509, -0.08], 13);
    // L.tileLayer(
    //   'https://api.mapbox.com/styles/v1/{id}/tiles/{z}/{x}/{y}?access_token=pk.eyJ1Ijoic3NhbmNoZXptYXJjIiwiYSI6ImNrZjVybWpocjBxNDYyeW5vZjA1M3ZoN3MifQ.XR8RGT-Ns7aky5hUbwzfCw',
    //   {
    //     attribution:
    //       'Map data &copy; <a href="https://www.openstreetmap.org/">OpenStreetMap</a> contributors, <a href="https://creativecommons.org/licenses/by-sa/2.0/">CC-BY-SA</a>, Imagery © <a href="https://www.mapbox.com/">Mapbox</a>',
    //     maxZoom: 18,
    //     id: 'mapbox/streets-v11',
    //     tileSize: 512,
    //     zoomOffset: -1,
    //     accessToken: 'your.mapbox.access.token',
    //   },
    // ).addTo(mymap);
    //
    // const polygon = L.polygon([
    //   [51.509, -0.08],
    //   [51.503, -0.06],
    //   [51.51, -0.047],
    // ]).addTo(mymap);
  }, []);

  return (
    <div>
      <Map center={center} zoom={13}>
        <TileLayer
          attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        <Circle center={center} fillColor="blue" radius={200} />
        <CircleMarker center={[51.51, -0.12]} color="red" radius={20}>
          <Popup>Popup in CircleMarker</Popup>
        </CircleMarker>
        <Polygon color="purple" positions={polygon} />
        <Polygon color="purple" positions={multiPolygon} />
      </Map>
      {/*<Head>*/}
      {/*  <title>My page title</title>*/}
      {/*  <link*/}
      {/*    rel="stylesheet"*/}
      {/*    href="https://unpkg.com/leaflet@1.7.1/dist/leaflet.css"*/}
      {/*    integrity="sha512-xodZBNTC5n17Xt2atTPuE1HxjVMSvLVW9ocqUKLsCC5CXdbqCmblAshOMAS6/keqq/sMZMZ19scR4PsZChSR7A=="*/}
      {/*    crossOrigin=""*/}
      {/*  />*/}
      {/*  <script*/}
      {/*    src="https://unpkg.com/leaflet@1.7.1/dist/leaflet.js"*/}
      {/*    integrity="sha512-XQoYMqMTK8LvdxXYG3nZ448hOEQiglfqkJs1NOQV44cWnUrBc8PkAOcXy20w0vlaXaVUearIOBhiXZ5V3ynxwA=="*/}
