import React, { useState } from 'react';
import { Header, Icon, Form, Button } from 'semantic-ui-react';
import { useRouter } from 'next/router';

import addLocation from './queries/addLocation';

export default function NewHarvests(): JSX.Element {
  const [name, setName] = useState('');
  const [area, setArea] = useState([]);
  const router = useRouter();

  const handleOnSubmit = async () => {
    await addLocation({ name, area });

    router.push('/locations');
  };

  return (
    <>
      <Header as="h1">
        <Icon name="map outline" />
        <Header.Content>New Harvest</Header.Content>
      </Header>

      <Form
        style={{
          padding: '0 100px',
        }}
        onSubmit={handleOnSubmit}
      >
        <Form.Field>
          <label>Name</label>
          <input placeholder="Name" onChange={(e): void => setName(e.target.value)} />
        </Form.Field>
        <Button type="submit" primary>
          Save
        </Button>
      </Form>
    </>
  );
}
