import Coordinate from '../models/Coordinate';
import styles from './Map';
import { Map as Mapp, TileLayer, Polygon, CircleMarker, Circle, Popup, Marker } from 'react-leaflet';

type MapProps = {
  initialLocation: Coordinate;
  polygon: Array<Coordinate>;
};

const center = [51.505, -0.09];

const polygon = [
  [51.515, -0.09],
  [51.52, -0.1],
  [51.52, -0.12],
];

const multiPolygon = [
  [
    [51.51, -0.12],
    [51.51, -0.13],
    [51.53, -0.13],
  ],
  [
    [51.51, -0.05],
    [51.51, -0.07],
    [51.53, -0.07],
  ],
];

const Map = ({ initialLocation }: MapProps): JSX.Element => {
  const [position, setPosition] = useState({ lat: 51.505, lng: -0.09 });
  const [zoom, setZoom] = useState(13);

  return (
    <Mapp center={position} zoom={zoom}>
      <TileLayer
        attribution='Map data Â© <a href="https://www.openstreetmap.org/">OpenStreetMap</a> contributors, <a href="https://creativecommons.org/licenses/by-sa/2.0/">CC-BY-SA</a>, Imagery (c) <a href="https://www.mapbox.com/">Mapbox</a>'
        url="https://api.mapbox.com/styles/v1/{id}/tiles/{z}/{x}/{y}?access_token={pk.eyJ1Ijoic3NhbmNoZXptYXJjIiwiYSI6ImNrZjVybWpocjBxNDYyeW5vZjA1M3ZoN3MifQ.XR8RGT-Ns7aky5hUbwzfCw}"
      />

      <Marker position={position}>
        <Popup>
          A pretty CSS3 popup. <br /> Easily customizable.
        </Popup>
      </Marker>
    </Mapp>
  );
};

export default Map;
