import React, { useEffect, useState } from 'react';
import Coordinate from '../../models/Coordinate';

import { Map as MapLeaflet, TileLayer, Polygon, CircleMarker, Circle, Popup, Marker } from 'react-leaflet';
import dynamic from 'next/dynamic';

type MapProps = {
  location: Array<Coordinate>;
};

const center = [51.505, -0.09];

const polygon = [
  [51.515, -0.09],
  [51.52, -0.1],
  [51.52, -0.12],
];

const multiPolygon = [
  [
    [51.51, -0.12],
    [51.51, -0.13],
    [51.53, -0.13],
  ],
  [
    [51.51, -0.05],
    [51.51, -0.07],
    [51.53, -0.07],
  ],
];

const Map = ({ location }: MapProps): JSX.Element => {
  const [position, setPosition] = useState({ lat: location[0].latitude, lng: location[0].longitude });

  const [zoom, setZoom] = useState(13);

  return (
    <MapLeaflet
      center={position}
      zoom={zoom}
      style={{
        height: '700px',
      }}
    >
      <TileLayer
        attribution='&copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
      />
    </MapLeaflet>
  );
};

export default Map;
