import React, { useEffect } from 'react';
import * as L from 'leaflet';
import Coordinate from '../models/Coordinate';
import styles from './Map';

type MapProps = {
  initialLocation: Coordinate;
  polygon: Array<Coordinate>;
};

const center = [51.505, -0.09];

const polygon = [
  [51.515, -0.09],
  [51.52, -0.1],
  [51.52, -0.12],
];

const multiPolygon = [
  [
    [51.51, -0.12],
    [51.51, -0.13],
    [51.53, -0.13],
  ],
  [
    [51.51, -0.05],
    [51.51, -0.07],
    [51.53, -0.07],
  ],
];

const Map = ({ initialLocation }: MapProps): JSX.Element => {
  const [map, setMap] = useEffect(null);
  useEffect(() => {
    setMap(L.map('mapid').setView([51.505, -0.09], 13));
  }, [map]);

  return (
    <div style={{ height: '300px' }} id="mapid"></div>
