import React, { useState } from 'react';
import { Map as MapLeaflet, TileLayer, Polygon } from 'react-leaflet';

import Location from '../../models/Location';

type MapProps = {
  location: Location;
};

function mapPositionFromCoordinates(coordinates) {
  return { lat: coordinates[0].latitude, lng: coordinates[0].longitude };
}

const Map = ({ location }: MapProps): JSX.Element => {
  const coordinates = location?.coordinates;

  const [position] = useState({ lat: coordinates[0].latitude, lng: coordinates[0].longitude });
  const [zoom] = useState(15);

  const polygon = location.map((coordinate) => [coordinate.latitude, coordinate.longitude]);

  return (
    <MapLeaflet
      center={position}
      zoom={zoom}
      style={{
        height: '700px',
      }}
    >
      <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
      <Polygon positions={polygon} />
    </MapLeaflet>
  );
};

export default Map;
